import React from "react";

export default class Button extends React.Component {
  render() {
    const style = {
      gridArea: this.props.id,
      alignSelf: "center",
      height: "100%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      background: "white",
      color: "rgb(38, 37, 39)",
      borderRadius: "1rem",
      boxShadow: "0 .1rem 1rem black"
    };

    return (
      <div
        style={style}
        onClick={this.props.click}
        type={this.props.type}
        value={this.props.value}
      >
        {this.props.value}
      </div>
    );
  }
}
