import React from "react";

export default class Display extends React.Component {
  render() {
    let regex = /[123456789.].*/g;
    let string = this.props.value.toString();
    let display = string.match(regex);
    if (!display) {
      display = 0;
    }
    return <div style={style}>{display}</div>;
  }
}

const style = {
  margin: "1rem",
  position: "relative",
  textAlign: "right",
  display: "flex",
  justifyContent: "flex-end",
  alignItems: "center",
  paddingRight: "1rem",
  border: ".5rem solid white",
  background: "rgb(30, 20, 20)",
  boxShadow: "inset 0 0rem 2rem black",
  borderRadius: ".5rem"
};
