import React from "react";
import Display from "./Display";
import Keypad from "./Keypad";

export default class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      subtotal: 0,
      number: 0,
      operator: "+",
      value: 0,
      isNewValue: true
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {


    e.target.blur();
  }

  componentDidMount(){
    document.addEventListener('keypress', (e)=>{
      console.log(e);
    })
  }

  updateState(type, operator){
    switch (type) {
      case "operator":
        var operator = e.target.getAttribute("value");
        if(!this.state.isNewValue){
        this.setState({ number: +this.state.value }, () => {
          var calc = 0;
          switch (this.state.operator) {
            case "+":
              calc = this.state.subtotal + this.state.number;
              break;
            case "-":
              calc = this.state.subtotal - this.state.number;
              break;
            case "*":
              calc = this.state.subtotal * this.state.number;
              break;
            case "/":
              calc = this.state.subtotal / this.state.number;
              break;
            case "=":
              calc = this.setState.subtotal;
            default:
              calc = 0;
          }
          if (operator == "=") {
            this.setState({
              subtotal: 0,
              value: calc,
              operator: "+",
              isNewValue: true
            });
          } else {
            this.setState({
              subtotal: calc,
              value: calc,
              operator: operator,
              isNewValue: true
            });
          }
        });
      }else{
        this.setState({subtotal: this.state.value, operator: operator}, ()=> console.log(this.state));
      }
      break;

      case "number":
        let value = e.target.getAttribute("value");
        if (this.state.isNewValue) {
          this.setState({ value: value, isNewValue: false });
        } else {
          this.setState({
            value: `${this.state.value}${value}`,
            isNewValue: false
          });
        }
        break;

      case "decimal":
        let string = this.state.value.toString();
        if (!string.match(/\./)) {
          this.setState({ value: `${this.state.value}.` });
        }
        break;

      case "clear":
        this.setState({
          subtotal: 0,
          value: 0,
          operator: "+",
          isNewValue: true
        });
        break;
      default:
        console.log("Error!");
    }
  }
  render() {
    return (
      <div className="calculator">
        <Display value={this.state.value} />
        <Keypad click={this.handleClick} />
      </div>
    );
  }
}
